    <script>
        
    function priceSwitch() {

        if($("#priceSwitch").is(":checked")){
                
                $("#pricing_id").val('{{env('STRIPE_PRODUCT_1_YEARLY')}}'); 
                $(".frequency").val('year');
                $(".pricing_html").html('$50 /Year');
           } else {
                $("#pricing_id").val('{{env('STRIPE_PRODUCT_1_MONTHLY')}}'); 
                $(".frequency").val('month'); 
                $(".pricing_html").html('$5 /Month'); 
               
           }

    }
        
    </script>
